		color: var(--main-color) !important;
		background: var(--hover-background) !important;
		border-color: var(--main-background) !important;
	}
	.unread-count::after
	{
		background: var(--main-color) !important;
		border-color: var(--main-color) !important;
	}

	/*Recent*/
	.listview .lv-header-alt
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.btn-primary
	{
		background: var(--main-color) !important;
		box-shadow: var(--shadow) !important;
		color: var(--main-text) !important;
	}
	#content [role="button"], button, .btn
	{
		color: var(--main-text) !important;
	}
	.btn-primary:hover
	{
		filter: brightness(110%) !important;
		color: var(--main-text) !important;
	}
	.btn-primary .fa
	{
		color: var(--main-text) !important;
	}
	.btn-default
	{
		background: var(--main-background) !important;
		box-shadow: var(--shadow) !important;
		color: var(--main-text) !important;
	}
	.btn-default:hover
	{
		color: var(--main-color) !important;
	}
	.dropdown-menu
	{
		background: var(--main-background) !important;
	}
	.dropdown-menu > li > a
	{
		color: var(--main-text) !important;
	}
	.dropdown-menu > li > a:hover
	{
		color: var(--main-color) !important;
		background: var(--hover-background) !important;
	}
	.dropdown-menu .divider
	{
		background: var(--hover-background) !important;
	}
	[component="category/topic"] .topic-title
	{
		color: var(--main-color) !important;
	}

	/*Search*/
	.form-control::-moz-placeholder, .fg-line .form-control:not(:disabled)
	{
		color: var(--dimmer-text) !important;
	}
	.fg-line:not([class*="has-"])::after
	{
		background: var(--main-color) !important;
	}
	select.form-control option
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
	}

	/*Checkboxes*/
	.checkbox .input-helper::before, .radio .input-helper::before
	{
		border-color: var(--dimmer-text) !important;
		border-radius: 0% !important;
	}
	.radio .input-helper::after
	{
		background: var(--main-color) !important;
		border-radius: 0% !important;
	}

	/*Tables*/
	.table-bordered
	{
		border: 1px solid var(--main-background) !important;
	}
	.table-bordered > thead > tr > td, .table-bordered > thead > tr > th
	{
		background: var(--second-background) !important;
		border-color: var(--main-background) !important;
	}
	.table-bordered > tbody > tr > td, .table-bordered > tbody > tr > th,
	.table-bordered > tfoot > tr > td, .table-bordered > tfoot > tr > th
	{
		border-color: var(--main-background) !important;
	}
	.table-striped > tbody > tr:nth-of-type(odd)
	{
		background: var(--main-background) !important;
	}
	.table-striped > tbody > tr:nth-of-type(even)
	{
		background: var(--second-background) !important;
	}

	/*Next buttons*/
	.pagination > li.active a, .pagination > li.active a:hover,
	.pagination > li > a, .pagination > li > span
	{
		border-color: var(--main-background) !important;
	}
	.pagination > li > a, .pagination > li > span
	{
		color: var(--main-text) !important;
	}
	.pagination > li.active a, .pagination > li.active a:hover,
	.pagination > li > a:hover, .pagination > li:hover > span
	{
		color: var(--main-color) !important;
	}

	/*Posts*/
	.topic .card .lv-body .post-content
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.groups.list .list-cover
	{
		background-image: url("https://forums.openusercss.org/assets/images/cover-default.png") !important;
	}
	.topic .card .lv-body .post-content::before, .topic .card .lv-body .post-content::after
	{
		border-right-color: var(--second-background) !important;
	}
	blockquote, hr
	{
		border-color: var(--hover-background) !important;
	}
	/* hack to prevent mouse hover to flicker */
	.persona-usercard .usercard-picture
	{
		pointer-events: none !important;
	}
	.persona-usercard .usercard-body
	{
		background: var(--main-background) !important;
	}

	/*Reply, quote, like etc*/
	.topic .post-tools a, .topic .card .lv-body .post-actions > li > a > i
	{
		color: var(--main-text) !important;
	}
	.topic .post-tools a:hover, .topic .card .lv-body .post-actions > li > a:hover > i
	{
		color: var(--main-color) !important;
	}
	.btn.btn-info.dropdown-toggle
	{
		background: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	/* fix emoji dropdown active item selection */
	.dropdown-menu .textcomplete-item.active
	{
		background: var(--hover-background) !important;
		cursor: default !important;
	}
	.dropdown-menu-right
	{
		background: var(--main-background) !important;
	}
	.dropdown-menu-right li:hover:not(.dropdown-header)
	{
		background: var(--hover-background) !important;
	}
	/*Tags*/
	.tag-container, .tag-container .tag-topic-count, .category > ul > li .tag-list .tag
	{
		border-color: var(--main-background) !important;
		background: var(--main-background) !important;
	}
	.timeago, .stats small, a.permalink, a.permalink .timeago:hover
	{
		color: var(--dimmer-text) !important;
		text-decoration: none !important;
	}
	.timeago:hover
	{
		color: var(--main-color) !important;
	}
	/*Left bar*/
	.category > ul > li:not(.unread) .card
	{
		border-color: var(--dimmer-text) !important;
	}
	/*Buttons*/
	.btn-group > .btn:hover
	{
		color: var(--main-text) !important;
	}
	div.post-bar .btn:hover, #content [role="button"]:hover
	{
		color: var(--main-text) !important;
	}
	.btn.btn-default.composer-discard
	{
		color: var(--main-color) !important;
	}
	.btn.btn-default.composer-discard:hover
	{
		background: transparent !important;
		background-image: none !important;
		filter: brightness(110%) !important;
	}
	.btn-danger
	{
		background-color: var(--warning-disable-all) !important;
		color: var(--main-text) !important;
	}
	/*Svgs next to the post*/
	.fa.fa-chevron-up, .fa.fa-chevron-down, .fa.fa-fw.fa-ellipsis-v
	{
		color: var(--dimmer-text) !important;
	}
	.fa.fa-chevron-up:hover, .fa.fa-chevron-down:hover, .fa.fa-fw.fa-ellipsis-v:hover
	{
		color: var(--main-color) !important;
	}

	/*Watching svg hover*/
	.actions > li > span > i
	{
		color: var(--main-text) !important;
	}
	.actions > li > span > i:hover
	{
		color: var(--main-color) !important;
		transition: none !important;
	}
	.visible-sm-inline.visible-md-inline.visible-lg-inline:hover, .caret:hover
	{
		color: var(--main-color) !important;
	}

	/*User page*/
	.card .card-header.ch-alt:not([class*="bgm-"])
	{
		background: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	#unfollow-btn
	{
		background: var(--main-background) !important;
		box-shadow: var(--shadow) !important;
		color: var(--main-text) !important;
	}
	#unfollow-btn:hover
	{
		color: var(--main-color) !important;
	}

	/*Reply, post messages*/
	body.page-compose.skin-default #content, body.page-compose.skin-noskin #content,
	.composer-container, .composer .tags-container .bootstrap-tagsinput
	{
		background: var(--main-background) !important;
		border-color: var(--hover-background) !important;
	}
	/* .title-container, .composer .title-container .title background
	is clearer this way and looks neat/clean layout*/
	.title-container, .composer .title-container .title
	{
		background: var(--second-background) !important;
	}
	[component="post"].highlight .post-body
	{
		border-color: var(--hover-background) !important;
	}
	.composer .formatting-bar .formatting-group .fa
	{
		color: var(--main-text) !important;
	}
	.composer .formatting-bar .formatting-group li:focus,
	.composer .formatting-bar .formatting-group li:hover,
	.composer .formatting-bar .formatting-group li:focus .fa,
	.composer .formatting-bar .formatting-group li:hover .fa
	{
		background: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	.composer .write-preview-container .write
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	/* preview well has subtle differences,
	lets keep them stylistic choices because they make sense. */
	.composer .preview.well
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
		opacity: .75
	}
	.composer .preview.well p
	{
		color: var(--dimmer-text) !important;
	}
	.composer .preview-container .help-text,
	.composer .write-container .help-text
	{
		color: var(--dimmer-text) !important;
		opacity: .7;
	}
	.composer .preview-container .help-text:hover,
	.composer .write-container .help-text:hover
	{
		color: var(--main-color) !important;
		opacity: 1;
	}
	.lv-body
	{
		padding-right: 20px !important;
	}
	.pull-right.post-edit
	{
		margin-top: -10px;
	}
	/*Avatar hover menu*/
	.popover
	{
		background-color: var(--main-background) !important;
		border-color: transparent !important;
		color: var(--main-text) !important;
	}

	pre
	{
		background-color: var(--main-background) !important;
		border: 1px solid var(--hover-background) !important;
	}
	code
	{
		background-color: var(--main-background) !important;
		border: none !important;
	}
}

