		--main-text: #fffdf5;
		--dimmer-text: #fff8e1;
		*/

		/*Ubuntu grey colors*/
		/*
		--main-color: #EF7847;
		--main-background: #312D2A;
		--second-background: #3D3C38;
		--hover-background: #59564D;
		--main-text: #F2F1EF;
		--dimmer-text: #E6E5E3;
		*/

		/*Ubuntu purple colors*/
		/*
		--main-color: #EF7847;
		--main-background: #2C071A;
		--second-background: #430B28;
		--hover-background: #520D30;
		--main-text: #F2F1EF;
		--dimmer-text: #E6E5E3;
		*/

		/*Orange*/
		/*
		--main-color: #ff6905;
		--main-background: #0a0400;
		--second-background: #0e0702;
		--hover-background: #110903;
		--main-text: #fff9f5;
		--dimmer-text: #ffede1;
		*/

		/*Jisho 夜明け colors https://userstyles.org/styles/115621/jisho*/
		/*
		--main-color: #EF7D6C;
		--main-background: #332222;
		--second-background: #2A1B1B;
		--hover-background: #863B2F;
		--main-text: #EFB26C;
		--dimmer-text: #986E3F;
		*/
	}

	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}

	a, a:visited, .style-name-link
	{
		background: transparent !important;
		color: var(--main-color) !important;
		opacity: 1;
	}
	a:hover, .style-name-link:hover
	{
		filter: brightness(110%);
	}
	/*Fix for visit profile button*/
	a[href*="profile"]
	{
		filter: brightness(100%) !important;
	}

	/* register dialog */
	.switch[data-v-688d0aeb]
	{
		background: var(--main-text) !important;
		border: 1px solid var(--main-text) !important;
	}
	label.checkbox:hover
	{
		color: var(--dimmer-text) !important;
	}
	/*help warning info banner */
	.mdi-alert:before, .mdi-information:before
	{
		color: var(--main-text) !important;
		pointer-events: none !important;
	}
	/* warning is orange */
	.notification.is-warning, .notification.is-warning p
	{
		background-color: var(--notice) !important;
		color: var(--main-background) !important;
	}
	/* danger is red */
	.notification.is-danger, .is-ancestor li
	{
		background-color: var(--warning-disable-all) !important;
		color: var(--main-text) !important;
	}
	.notification.is-danger p, .notification.is-danger a
	{
		color: var(--main-text) !important;
	}
	/* target section hr directly */
	.section hr
	{
		background: var(--hover-background) !important;
	}

	/* loading */
	.ouc-loading-cover, .ouc-quick-loading, .ouc-loading-inner
	{
		background: var(--main-color) !important;
	}

	/* website profile */
	.ouc-profile-wrapper h1
	{
		color: var(--main-text) !important;
	}
	/* this styles the bio preview pane below and the theme submission preview pane
	Im taking inspiration from the post submission preview well
	commit d021dc9 or https://git.io/vhKn1 */
	.columns > div > div > div:nth-child(4), .box.is-minheight-description
	{
		background: var(--main-background) !important;
		color: var(--main-text) !important;
		opacity: .75;
	}
	.column.is-6  div:nth-child(4)
	{
		background: transparent !important;
		opacity: 1 !important;
	}
	.content p, .box.is-minheight-description p
	{
		color: var(--dimmer-text) !important;
	}

	/* help page */
	.box.is-warning
	{
		background-color: var(--notice) !important;
		color: var(--main-background) !important;
	}
	.is-inline a
	{
		color: var(--main-background) !important;
	}
	.is-inline a:hover
	{
		filter: contrast(70%) !important;
	}
	svg.is-inline
	{
		color: var(--second-background) !important;
	}

	/*Header*/
	.navbar.is-primary, .navbar-menu.is-primary, .navbar.is-primary .navbar-brand > .navbar-item
	{
		background: var(--main-background) !important;
		color: var(--main-text) !important;
	}
	/*Header items*/
	.navbar-item:not(.no-active):not(.router-link-exact-active):not(:hover)
	{
		background: transparent !important;
		color: var(--main-text) !important;
	}
	.navbar-item.router-link-exact-active.router-link-active
	{
		background: var(--second-background) !important;
		color: var(--main-color) !important;
	}
	.navbar-item.router-link-exact-active.router-link-active:hover, .navbar-item:hover
	{
		background: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	/*Beta*/
	.tag:not(body).is-secondary
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}

	/* 404 page */
	.container h1, .container h4
	{
		color: var(--main-text) !important;
	}

	/*Body of the page*/
	.ouc-app-root
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	/*Footer*/
	.footer
	{
		background: var(--hover-background) !important;
		color: var(--main-text) !important;
	}

	/*Popular themes*/
	.box
	{
		background: var(--hover-background) !important;
		color: var(--dimmer-text) !important;
	}

	/*Newest themes*/
	.is-primary > .box
	{
		background: var(--hover-background) !important;
		color: var(--dimmer-text) !important;
	}
	h4
	{
		color: var(--main-color) !important;
	}
	h6
	{
		color: var(--main-text) !important;
	}

	/*Themes*/
	h1
	{
		color: var(--main-color) !important;
	}
	/*About*/
	h2, .label, .card-header-title label, .card-header-title p
	{
		color: var(--main-text) !important;
	}
	/*Buttons*/
	.content.is-marginless .button.is-primary, .level-left .button.is-primary
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: 1 !important;
		box-shadow: var(--shadow) !important;
	}
	.content.is-marginless .button.is-primary:hover, .level-left .button.is-primary:hover
	{
		filter: brightness(110%);
	}
	.level-left .button.is-primary a, .level-left .button.is-primary p
	{
		color: var(--main-text) !important;
	}
	/*Theme options*/
	li, p, ul
	{
		color: var(--dimmer-text) !important;
	}
	/* generic hr line */
	hr
	{
		background: var(--hover-background) !important;
	}
	/* user card .card-content.is-brand-primary content hr*/
	.is-8 hr
	{
