		*/

		/*Firefox Dark colors*/
		/*
		--main-color: #5675B9;
		--main-background: #272B35;
		--second-background: #181D20;
		--hover-background: #353A44;
		--main-text: #E3EEF9;
		--dimmer-text: #606376;
		*/

		/*Firefox57 Dark colors*/
		/*
		--main-color: #4080FB;
		--main-background: #0C0C0D;
		--second-background: #252526;
		--hover-background: #323234;
		--main-text: #F9F9FA;
		--dimmer-text: #D0D0D0;
		*/

		/*Discord colors*/
		/*
		--main-color: #7289DA;
		--main-background: #1E2124;
		--second-background: #2F3136;
		--hover-background: #484B51;
		--main-text: #FFFFFF;
		--dimmer-text: #ADA8AA;
		*/

		/*YouTube dark colors*/
		/*
		--main-color: #E52117;
		--main-background: #111111;
		--second-background: #232323;
		--hover-background: #343434;
		--main-text: #E1E1E1;
		--dimmer-text: #7F7F7F;
		*/

		/*Mint-Y-Dark colors*/
		/*
		--main-color: #9AB87C;
		--main-background: #2F2F2F;
		--second-background: #383838;
		--hover-background: #404040;
		--main-text: #FFFFFF;
		--dimmer-text: #D5DADA;
		*/

		/*9anime colors*/
		/*
		--main-color: #723f8c;
		--main-background: #0B0A0D;
		--second-background: #17151C;
		--hover-background: #1E1C25;
		--main-text: #B4B4B4;
		--dimmer-text: #747474;
		*/

		/*Black and white*/
		/*
		--main-color: #ffffff;
		--main-background: #000000;
		--second-background: #0a0a0a;
		--hover-background: #0f0f0f;
		--main-text: #b4b4b4;
		--dimmer-text: #828282;
		*/

		/*Yellow (colors from petrocompletions)*/
		/*
		--main-color: #FFC700;
		--main-background: #141414;
		--second-background: #202222;
		--hover-background: #353838;
		--main-text: #EFF0F1;
		--dimmer-text: #9F9999;
		*/

		/*Yellow 2*/
		/*
		--main-color: #ffc700;
		--main-background: #0a0800;
		--second-background: #0c0a04;
		--hover-background: #0f0d05;
		--main-text: #fffdf5;
		--dimmer-text: #fff8e1;
		*/

		/*Ubuntu grey colors*/
		/*
		--main-color: #EF7847;
		--main-background: #312D2A;
		--second-background: #3D3C38;
		--hover-background: #59564D;
		--main-text: #F2F1EF;
		--dimmer-text: #E6E5E3;
		*/

		/*Ubuntu purple colors*/
		/*
		--main-color: #EF7847;
		--main-background: #2C071A;
		--second-background: #430B28;
		--hover-background: #520D30;
		--main-text: #F2F1EF;
		--dimmer-text: #E6E5E3;
		*/

		/*Orange*/
		/*
		--main-color: #ff6905;
		--main-background: #0a0400;
		--second-background: #0e0702;
		--hover-background: #110903;
		--main-text: #fff9f5;
		--dimmer-text: #ffede1;
		*/

		/*Jisho 夜明け colors https://userstyles.org/styles/115621/jisho*/
		/*
		--main-color: #EF7D6C;
		--main-background: #332222;
		--second-background: #2A1B1B;
		--hover-background: #863B2F;
		--main-text: #EFB26C;
		--dimmer-text: #986E3F;
		*/
	}

	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}

	/*Outline for links*/
 	a
 	{
		outline-color: var(--main-color) !important;
		text-decoration: none !important;
	}

	/*Body*/
	body
	{
		background-color: var(--main-background) !important;
	}

	/*Logo*/
	.logo
	{
		filter: grayscale(100%) brightness(300%);
	}

	/*Header*/
	#header
	{
		background-color: var(--hover-background) !important;
	}
	#search_input
	{
		background-color: var(--second-background) !important;
	}
	#input_text_search
	{
		color: var(--main-text) !important;
	}
	.Combined-Shape[src*="blue"]
	{
		filter: brightness(50%);
	}
	.Combined-Shape[src*="blue"]
	{
		filter: grayscale(100%) brightness(200%);
	}
	#create_new_style a
	{
		color: var(--main-text) !important;
	}
	#create_new_style
	{
		color: var(--main-text) !important;
		border-color: var(--hover-background) !important;
		background-color: var(--main-background) !important;
	}
	#create_new_style:hover a
	{
		color: var(--main-color) !important;
	}
	.header_button a
	{
		color: var(--main-text) !important;
	}
	.header_button a:hover
	{
		color: var(--main-color) !important;
	}

	/*Sidebar*/
	.navigation
	{
		background-color: var(--main-background) !important;
	}
    .navigation .links-box .navigation-link:hover
    {
        background-color: var(--hover-background) !important;
    }
	.navigation .links-box .navigation-link, .navigation .links-box
	{
	    box-shadow: 0 1px 0 0 var(--hover-background), 0 0 0 0 var(--hover-background) !important;
	}
	.navigation .links-box .navigation-link .navigation-title
	{
		color: var(--main-text) !important;
	}
	.navigation .links-box .navigation-link .logo-navigation-link[src*="svg"]
	{
        filter: invert(1) brightness(200%) grayscale(1);
	}


	/*Tabs*/
	.navigation_link:hover
	{
	    background-color: var(--hover-background) !important;
	}
	.navigation_link_active
	{
	    border-color: var(--main-color) !important;
	}
	.navigation_title
	{
		color: var(--main-text) !important;
	}
	.navigation_link:hover .navigation_title
	{
		color: var(--main-color) !important;
	}
	.logo_navigation_link[src*="svg"]
	{
		filter: invert(100%) grayscale(100%);
	}


	/*Slideshow*/
	#left-arrow-carousel, #right-arrow-carousel
	{
		filter: invert(100%);
	}
	#overlay-info
	{
		background-color: var(--second-background) !important;
	}
	#style-info-title-carousel
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	#style-info-author-carousel a
	{
		color: var(--main-text) !important;
	}
	#style-info-author-carousel a:hover, #style-info-title-carousel:hover
	{
		color: var(--main-color) !important;
		opacity: 1;
	}
	#overlay-info
	{
		border-radius: 0 !important;
	}
	.oval
	{
		background-color: var(--dimmer-text) !important;
	}
	.oval-active
	{
		background-color: var(--main-color) !important;
		box-shadow: var(--shadow) !important;
	}


	/*Categories*/
	.category_title
	{
		color: var(--main-text) !important;
	}
	.see_more
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		box-shadow: var(--shadow) !important;
	}
	.see_more:hover
	{
		color: var(--main-color) !important;
	}


	/*Cards*/
	.us-stylecard, .us-stylecard--short, .us-stylecard--long
	{
		background-color: var(--second-background) !important;
	}
	.us-stylecard--short .us-stencil
	{
		background-color: var(--hover-background) !important;
		box-shadow: 0 1px 0 0 var(--hover-background), 0 0 0 0 var(--hover-background) !important;
	}
	.us-stylecard--short .us-updated
	{
		color: var(--main-text) !important;
	}
	.us-stylecard--short .us-title > a, .us-stylecard--long .us-title > a
	{
		color: var(--main-color) !important;
	}
	.downloads-icon
	{
		filter: grayscale(100%) brightness(200%);
	}
	.tooltip_rating .tooltiptext_rating, .tooltip .tooltiptext
	{
		background-color: var(--second-background) !important;
		color: var(--main-text) !important;
	}


	/*Footer*/
	#footer
	{
		background-color: var(--hover-background) !important;
	}
	#copyrights
	{
		color: var(--main-color) !important;
	}
	.footer_link a, #bottomLinks, .footer_link a:visited
	{
		color: var(--main-text) !important;
	}
	.footer_link a:hover
	{
		color: var(--main-color) !important;
	}


	/*Editor's choice*/
	h1
	{
		color: var(--main-text) !important;
	}
	.view_mode_img[src="/ui/images/icons/squares-on.svg"], .view_mode_img[src="/ui/images/icons/rectangle-on.svg"]
	{
		filter: grayscale(100%) brightness(200%);
	}
	.current, .div-arrow
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		border: none !important;
		opacity: .9;
	}
	.pageNumber
	{
		color: var(--main-text) !important;
	}
	.pageNumber:hover
	{
		color: var(--main-color) !important;
	}
	.div-arrow:hover, .current:hover
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: 1;
	}

	.arrow-icon[src="/ui/images/icons/right-arrow-icon.svg"], .arrow-icon[src="/ui/images/icons/left-arrow-icon.svg"]
	{
		filter: grayscale(100%) brightness(200%);
	}
	.style_card_long, .line_seperator
	{
		box-shadow: inset 0 -1px 0 0 var(--hover-background) !important;
	}
	#title_long a, .style_card_long #title_long a
	{
		color: var(--main-text) !important;
	}
	#title_long a:hover, .style_card_long:hover #title_long a, .style_author_long a
	{
		color: var(--main-color) !important;
	}
	.style_description_long, .style_author_long
	{
		color: var(--dimmer-text) !important;
	}
	.view_details
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: .9;
	}
	.view_details:hover
	{
		background-color: var(--main-color) !important;
		color: var(--main-text) !important;
		opacity: 1;
	}


	/*Style*/
	#preview_image_div
	{
		background-position: center center !important;
	}
	.author-name a, .text_style_page a, #as_userscript a, #notes_textarea a, #discussion_author a
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	.author-name a:hover, .text_style_page a:hover, #as_userscript a:hover,
	#as_userscript a:visited, #as_userscript a:visited:hover, #notes_textarea a:hover,
	#notes_textarea a:visited, #discussion_author a:visited, #discussion_author a:visited:hover,
	#discussion_author a:hover
	{
		color: var(--main-color) !important;
		opacity: 1;
	}
	#share_div, #headline, #infomation_value_left, #information_key, .title_setting
	{
		color: var(--main-text) !important;
	}
	#information_key_left, .text_style_page, #discussion_date, .by-author, .style_author.ellipsis
	{
		color: var(--dimmer-text) !important;
	}
	#information_value, #applies_link
	{
		color: var(--main-color) !important;
	}
	.white_button:hover, .css_button:hover, .advanced_button:hover, #archive_style_button:hover,
	#send_feedback_button:hover, #style_installed_button
	{
		background-color: var(--hover-background) !important;
		color: var(--main-color) !important;
		border: none !important;
		box-shadow: var(--shadow) !important;
	}
	.white_button, .css_button, .advanced_button, #archive_style_button, #send_feedback_button,
	#style_installed_button:hover
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		border: none !important;
		box-shadow: var(--shadow) !important;
	}
	#update_style_button, #install_style_button
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		border: none !important;
		box-shadow: var(--shadow) !important;
	}
	#update_style_button:hover, #install_style_button:hover
	{
		color: var(--main-color) !important;
	}
	.installStyleButtonTooltip
	{
		background-color: var(--hover-background) !important;
	}
	#notes_textarea, #stylish-code, .advancedsettings_shown
	{
		background-color: var(--second-background) !important;
		color: var(--main-text) !important;
		border: 1px solid var(--hover-background) !important;
	}
	div.setting_div > div > div
	{
		background-color: black !important;
	}
	#single_discussion
	{
		box-shadow: 0 1px 0 0 var(--hover-background), 0 0 0 0 var(--hover-background) !important;
	}


	/*iframes*/
	p, ul, i, body, .contact-bold-font
	{
		color: var(--dimmer-text) !important;
	}
	a, a:visited, .sub-title
	{
		color: var(--main-color) !important;
		opacity: .9 !important;
	}
	a:hover, a:visited:hover
	{
		color: var(--main-color) !important;
		opacity: 1 !important;
	}
	.contact-font
	{
		color: var(--dimmer-text) !important;
	}
	.contact-input
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	:focus
	{
		outline-color: var(--main-color) !important;
	}
	/*login*/
	.btn-auth, input#login-existing, input#password-existing, input[type="submit"]
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--dimmer-text) !important;
	}
	.btn-auth:hover, input[type="submit"]:hover
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	input#remember-normal
	{
		filter: invert(100%);
	}
	a.btn-auth:visited
	{
		color: var(--dimmer-text) !important;
	}

	/*Edit style page*/
	input:not([class*="color"]), textarea
	{
		background-color: var(--second-background) !important;
		border: 1px solid var(--hover-background) !important;
		color: var(--dimmer-text) !important;
	}
	input:active, textarea:active, input:focus, textarea:focus
	{
        	border-color: var(--main-color) !important;
	}
	input[id*="option-default"]
	{
        	border: 1px solid transparent !important;
		box-shadow: var(--shadow) !important
	}
	.edit-setting
	{
		background-color: var(--hover-background) !important;
	}
	/*If your browser shows buttons white because of a default white theme, uncomment this*/
	/*
	input[type="radio"], input#enable-source-editor-code
	{
		filter: invert(100%);
	}
	*/

	/*Edit css*/
	.CodeMirror
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.CodeMirror-linenumber, .CodeMirror-gutters
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.errorExplanation
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}

	/*Code text*/
	.CodeMirror div.CodeMirror-cursor { border-color: var(--main-color); }
	.cm-s-default.CodeMirror { background: var(--second-background); color: var(--main-text); }
	.cm-s-default div.CodeMirror-selected { background: var(--hover-background); }
	.cm-s-default .CodeMirror-line::selection, .cm-s-default .CodeMirror-line > span::selection, .cm-s-default .CodeMirror-line > span > span::selection { background: var(--hover-background); }
	.cm-s-default .CodeMirror-line::-moz-selection, .cm-s-default .CodeMirror-line > span::-moz-selection, .cm-s-default .CodeMirror-line > span > span::-moz-selection { background-color: var(--hover-background); }
	.cm-s-default .CodeMirror-gutters { background: var(--hover-background); border-right: 0px; }
	.cm-s-default .CodeMirror-guttermarker { color: var(--main-text); }
	.cm-s-default .CodeMirror-guttermarker-subtle, .cm-s-default .CodeMirror-linenumber { color: var(--dimmer-text); }
	.cm-s-default .CodeMirror-cursor { border-left: 1px solid var(--main-color); }

	.cm-s-default span.cm-comment { color: var(--dimmer-text); }
	.cm-s-default span.cm-atom, .cm-s-default span.cm-number { color: var(--main-color); opacity: .6; }

	.cm-s-default span.cm-property, .cm-s-default span.cm-attribute { color: var(--main-color); opacity: 1; }
	.cm-s-default span.cm-keyword, .cm-s-default span.cm-string { color: var(--main-color); filter: hue-rotate(60deg); }
	.cm-s-default span.cm-string.cm-property { color: var(--main-text); }

	.cm-s-default span.cm-variable { color: var(--main-text); }
	.cm-s-default span.cm-variable-2 { color: var(--main-color); opacity: .6; }
	.cm-s-default .cm-variable-3, .cm-s-default .cm-type { color: var(--main-color); filter: hue-rotate(280deg); }
	.cm-s-default span.cm-def { color: var(--main-text); }
	.cm-s-default span.cm-bracket { color: var(--main-text); font-weight: bold; }
	.cm-s-default span.cm-tag { color: var(--main-color); opacity: .6; }
	.cm-s-default span.cm-link, .cm-s-default span.cm-keyword { color: var(--main-color); filter: invert(100%); }
	.cm-s-default span.cm-error { border-bottom: var(--hover-background); color: var(--main-text); }
	.cm-s-default span.cm-qualifier { color: var(--main-text); }

	.cm-s-default .CodeMirror-activeline-background { background: var(--second-background); }
	.cm-s-default .CodeMirror-matchingbracket { color: var(--main-color); }
	.cm-s-default .CodeMirror-matchingtag { background: var(--dimmer-text); }

	.cm-s-default .cm-builtin { color: var(--main-color); }
	.cm-s-default .cm-meta { color: var(--dimmer-text); }


	/*Forum*/
	#Head
	{
    	background: var(--hover-background) !important;
    	color: var(--main-text) !important;
	}
	#Head a:hover
	{
		color: var(--main-color) !important;
	}


	/*Search*/
	input.DateBox, input.InputBox, input.SmallInput, textarea
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	.ItemDiscussion .Meta, .DataList .Meta
	{
		color: var(--dimmer-text) !important;
	}
	mark
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	#search_input input
	{
		border: none !important;
	}
	.SiteSearch .Button
	{
		background: url('https://forum.userstyles.org/applications/dashboard/design/images/sprites.png') 0 -324px no-repeat transparent !important;
	}
	.SiteSearch .Button:hover
	{
		background: url('https://forum.userstyles.org/applications/dashboard/design/images/sprites.png') 0 -260px no-repeat transparent !important;
	}

	#title_div
	{
		box-shadow: inset 0 -1px 0 0 var(--hover-background) !important;
	}

	/*Login*/
	a.TextColor, a .TextColor
	{
		color: var(--main-text) !important;
	}
	a.TextColor:hover, a .TextColor:hover, a:hover .TextColor
	{
		color: var(--main-color) !important;
	}
	.Button, .customize_button
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--dimmer-text) !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}
	.Button:hover, .customize_button:hover
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-color) !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}


	/*Menu*/
	.Sprite16
	{
		filter: invert(100%);
	}
	.Alert
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}


	/*Categories*/
	.PanelCategories li:first-child, .PanelCategories li:nth-last-child(2)
	{
		border-color: var(--hover-background) !important;
	}
	.Count
	{
		background: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	#Panel .FilterMenu li a, .PanelInfo li a
	{
		color: var(--main-text) !important;
	}
	#Panel .FilterMenu li a:hover, .PanelInfo li a:hover
	{
		color: var(--main-color) !important;
	}


	/*Discussions*/
	.Tag
	{
		background: var(--main-background) !important;
		color: var(--main-text) !important;
	}
	.HasNew
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	.MItem, span.ShowDiscussionAuthor, .Pager span
	{
		color: var(--dimmer-text) !important;
	}
	.OptionsTitle, a.Bookmark, a.Bookmarked, a.Bookmarking
	{
		filter: invert(80%);
	}
	.OptionsTitle:hover, a.Bookmark:hover, a.Bookmarked:hover, a.Bookmarking:hover
	{
		filter: invert(100%);
	}
	.MenuItems
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: none !important;
	}
	.Flyout:before, .Flyout:after
	{
		border-bottom-color: var(--second-background) !important;
	}
	.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active
	{
		color: var(--main-text) !important;
	}
	.MenuItems a:hover
	{
		background-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	.MenuItems hr
	{
		border-bottom-color: var(--hover-background) !important;
	}
	.DataList .ItemDiscussion
	{
		margin-bottom: 10px !important;
		box-shadow: var(--shadow) !important;
		background: var(--second-background) !important;
	}
	.Item.Read
	{
		background: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.Item.Read:hover, .DataList .ItemDiscussion:hover
	{
		background: var(--hover-background) !important;
	}
	.ItemContent.Discussion
	{
		margin-left: 10px !important;
	}
	.Item.Read .Title a, .Item.Read .BlockTitle a
	{
		color: var(--main-text) !important;
		font-weight: bold;
	}
	.Item.Read .Title a:hover, .Item.Read .BlockTitle a:hover
	{
		color: var(--main-color) !important;
	}


	/*Replacing images in the forum and users' style page*/
	.DataList .rating-image, .good-average-rating, .ok-average-rating, .bad-average-rating
	{
		display: block !important;
		-moz-box-sizing: border-box !important;
		box-sizing: border-box !important;
		width: 24px !important;
		height: 24px !important;
		padding-left: 24px !important;
		right: -175px !important;
	}
	.style-warning-icon
	{
		-moz-box-sizing: border-box !important;
		box-sizing: border-box !important;
		width: 12px !important;
		height: 12px !important;
		padding-left: 12px !important;
		right: -175px !important;
		margin: 5px auto 5px 5px;
	}
	.DataList .rating-image
	{
		left: 5px;
		top: 6px;
	}
	.good-average-rating, .ok-average-rating, .bad-average-rating
	{
		margin-left: auto;
		margin-right: auto;
	}
	.rating-image.bad-rating, .bad-average-rating
	{
		background-image: url("https://gitlab.com/RaitaroH/Userstyles-DeepDark/raw/master/Images/bad.png");
	}
	.rating-image.good-rating, .good-average-rating
	{
		background-image: url("https://gitlab.com/RaitaroH/Userstyles-DeepDark/raw/master/Images/good.png");
	}
	.rating-image.ok-rating, .ok-average-rating
	{
		background-image: url("https://gitlab.com/RaitaroH/Userstyles-DeepDark/raw/master/Images/ok.png");
	}
	.style-warning-icon
	{
		background-image: url("https://gitlab.com/RaitaroH/Userstyles-DeepDark/raw/master/Images/warning.png");
	}
	.author-styles a[href*="edit"], .author-styles a[href*="delete"], .author-styles a[href*="stats"]
	{
		visibility: hidden;
		position: relative;
		width: 24px !important;
		height: 24px !important;
		display: inline-block;
	}
	.author-styles a[href*="edit"]:after, .author-styles a[href*="delete"]:after, .author-styles a[href*="stats"]:after
	{
		visibility: visible;
		position: absolute;
		top: 0;
		left: 0;
		width: 24px !important;
		height: 24px !important;
		display: inline-block;
	}
	.author-styles a[href*="edit"]:after
	{
		content: url("https://gitlab.com/RaitaroH/Userstyles-DeepDark/raw/master/Images/edit.png");
	}
	.author-styles a[href*="delete"]:after
	{
		content: url("https://gitlab.com/RaitaroH/Userstyles-DeepDark/raw/master/Images/delete.png");
	}
	.author-styles a[href*="stats"]:after
	{
		content: url("https://gitlab.com/RaitaroH/Userstyles-DeepDark/raw/master/Images/stats.png");
	}
	.author-styles > tbody:nth-child(2) > tr:nth-child(1) > td:nth-child(8)
	{
		width: 100px;
	}

	/*User styles*/
	.statistic table, th, td
	{
		border: none !important;
		background-color: var(--second-background) !important;
	}
	.numeric-value
	{
		text-align: center;
	}
	td[valign="top"]
	{
		vertical-align: middle;
		padding-left: 10px;
	}
	/*Removed the empty space*/
	.author-styles > thead:nth-child(1) > tr:nth-child(1) > th:nth-child(1)
	{
		opacity: 0;
		border: none !important;
	}
	.author-styles tbody tr, .author-styles tbody tr td
	{
		border-bottom: 10px solid var(--main-background) !important;
		box-shadow: var(--shadow) !important;
	}
	.author-styles tbody tr:hover td
	{
		background-color: var(--hover-background) !important;
	}
	.author-styles > tbody:nth-child(2) > tr:nth-child(1) > td:nth-child(7)
	{
		width: 100px;
	}
	.style-warning
	{
		background-color: var(--hover-background) !important;
	}
	td[class="obsolete"] a
	{
		text-decoration: line-through !important;
	}

	.Pager .Highlight, .Pager a:hover
	{
		color: var(--main-color) !important;
	}
	.Pager a
	{
		color: var(--main-text) !important;
	}
	.DataList .ItemDiscussion:hover
	{
		background-color: var(--hover-background);
	}
	.BoxButtons.BoxNewDiscussion a
	{
		color: var(--dimmer-text) !important;
	}
	.BoxButtons.BoxNewDiscussion:hover a
	{
		color: var(--main-color) !important;
	}

	/*Preferences*/
	table.PreferenceGroup th, table.PreferenceGroup td, #Form_Invitation th, #Form_Invitation td
	{
		border-color: var(--hover-background) !important;
	}
	table.PreferenceGroup:hover th, table.PreferenceGroup:hover td, #Form_Invitation:hover th,
	#Form_Invitation:hover td
	{
		background-color: transparent !important;
	}
	.WarningMessage, .Info
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	select
	{
		background-color: var(--hover-background);
		border-color: var(--hover-background);
		color: var(--dimmer-text);
	}


	/*Notifications*/
	.Flyout:before, .Flyout:after
	{
		border-bottom-color: var(--hover-background) !important;
	}

	/*Comments*/
	.Attachment:hover .FileHover
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.ReactSprite
	{
		filter: invert(100%);
	}
	blockquote.Quote, blockQuote.UserQuote, code, pre
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	code, pre
	{
		color: var(--main-text) !important;
	}
	.EmoticonBox, .EmoticonContainer, .EmotifyDropdownActive
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: none !important;
	}
	.EmoticonBox:hover
	{
		box-shadow: 1px 1px 1px 1px var(--second-background) !important;
	}
	.FlyoutMenu
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--dimmer-text) !important;
	}
	.InformMessages .InformMessage
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}
	.InformMessages .InformMessage a.Close
	{
		color: var(--main-text) !important;
	}
	.InformMessages .InformMessage a.Close:hover
	{
		color: var(--main-color) !important;
	}

	/*Profile*/
	.NavButton, a.NavButton.ProfileButtons.Button-EditProfile
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--dimmer-text) !important;
		box-shadow: none !important;
		text-shadow: none !important;
	}
	.NavButton:hover, a.NavButton.ProfileButtons.Button-EditProfile:hover
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		color: var(--main-color) !important;
		box-shadow: none !important;
	}
	.SpEditProfile
	{
		filter: invert(100%);
	}
	dl.About dt
	{
		color: var(--dimmer-text) !important;
	}


	/*New Discussion*/
	.DiscussionForm label
	{
		color: var(--dimmer-text) !important;
	}


	/*Messages*/
	a.Button.NewConversation.Primary, #ConversationForm label
	{
		color: var(--dimmer-text) !important;
	}
	a.Button.NewConversation.Primary:hover
	{
		color: var(--main-color) !important;
	}
	ul.token-input-list, div.token-input-dropdown, div.token-input-dropdown ul li,
	div.token-input-dropdown ul li.token-input-dropdown-item, div.token-input-dropdown ul li.token-input-dropdown-item2
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: none !important;
	}
	div.token-input-dropdown ul li:hover, div.token-input-dropdown ul li.token-input-dropdown-item:hover,
	div.token-input-dropdown ul li.token-input-dropdown-item2:hover
	{
		background: var(--hover-background) !important;
		color: var(--main-color) !important;
	}
	li.token-input-token
	{
		border-color: var(--hover-background) !important;
	}

	#token-input-Form_To
	{
		border: none !important;
	}

	/*Comment preview*/
	.Preview
	{
		background: var(--hover-background) !important;
		color: var(--main-text) !important;
	}

	/*Android page*/
	#top_android_button
	{
		background-color: var(--main-background) !important;
		border: none !important;
		box-shadow: var(--shadow) !important;
	}
	#top_android_button .android_button_text
	{
		color: var(--main-text) !important;
	}
	#top_android_button:hover .android_button_text
	{
		color: var(--main-color) !important;
	}
	#landing_page_div .top_landing_page_div, #nav-view-responsive
	{
		background: var(--main-background) !important;
	}
	#landing_page_div .bottom_landing_page_div
	{
		background: var(--second-background) !important;
	}
	#landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_subtitle
	{
		color: var(--main-color) !important;
	}
	#landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_text,
	#landing_page_div .bottom_landing_page_div .android_feature_div .android_feature_text div
	{
		color: var(--main-text) !important;
	}
	#landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_helpus
	{
		color: var(--dimmer-text) !important;
	}
	#landing_page_div .top_landing_page_div .coming_soon_div .get_it_now
	{
		background: var(--main-color) !important;
		border-color: var(--main-color) !important;
		box-shadow: none !important;
	}

	/*FAQ page*/
	.faq-page-title
	{
		color: var(--main-color) !important;
	}
	.faq-page-flex-subtitle, .faq-page-subtitle
	{
		color: var(--main-text) !important;
	}
	.faq-page-flex-text, .faq-page-text
	{
		color: var(--dimmer-text) !important;
	}

sourceCode:
/* ==UserStyle==
@name Gmail DeepDark
@namespace github.com/RaitaroH/Gmail-DeepDark
@homepageURL https://github.com/RaitaroH/Gmail-DeepDark
@version 1.1.1
@updateURL https://rawgit.com/RaitaroH/Gmail-DeepDark/master/GmailDeepDark.user.css
@description Emails should be written and read in the dark. May the dark be kinder on thine eyes. (Gmail dark theme)
@author RaitaroH
@license GNU-V3.0

@preprocessor stylus

@var select stylus-deepdark-style "Preset themes" [
	"Deep-Dark",
	"Inspired-Dark",
	"Breeze-Dark",
	"HavocOS",
	"Arc-Dark",
	"Vertex-Dark",
	"Mint-Y-Dark",
	"Adapta-Nokto",
	"Adapta-Breath-Nokto",
	"Gruvbox-Dark",
	"Gruvbox-Light",
	"NierAutomata-Dark",
	"NierAutomata-Light",
	"Solarized-Dark",
	"Solarized-Light",
	"9anime",
	"Firefox-Dark",
	"Firefox-57",
	"Discord",
	"YouTube-Dark",
	"Black-and-White",
	"Yellow",
	"Yellow-2",
	"Ubuntu-Grey",
	"Ubuntu-Purple",
	"Orange",
	"Jisho",
	"Custom"
]

@var color mainColor "Custom main color" "#367bf0"
@var color mainBackground "Custom main background" "#22242d"
@var color secondBackground "Custom second background" "#242730"
@var color hoverBackground "Custom hover background" "#4e5467"
@var color mainText "Custom main text" "#eee"
@var color dimmerText "Custom second text" "#ccc"
@var color colorShadow "Custom shadow color" "rgba(56, 60, 74, .3)"

==/UserStyle== */

@-moz-document domain("mail.google.com"), domain("hangouts.google.com") {

/*For the new Material Design for Gmail*/

/*Theme made by RaitaroH @https://github.com/RaitaroH/Gmail-DeepDark*/

/*GNU General Public License v3.0.*/

	/*Main color variables*/
	:root
	{
		if stylus-deepdark-style == "Deep-Dark" {
			--main-color: #00adee;
			--main-background: #111;
			--second-background: #181818;
			--hover-background: #232323;
			--main-text: #eff0f1;
			--dimmer-text: #ccc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .3);
		}
		else if stylus-deepdark-style == "Inspired-Dark" {
			--main-color: #5e8acc;
			--main-background: #232629;
			--second-background: #181818;
			--hover-background: #515254;
			--main-text: #eee;
			--dimmer-text: #ccc;
