    color: var(--main-text) !important;
  }
  ::-moz-selection {
    background: var(--main-color) !important;
    color: var(--main-text) !important;
  }
  a {
    outline-color: var(--main-color) !important;
  }
  a:hover {
    text-decoration: none !important;
  }
  .svg .header__logo,
  .svg.dark-header .header__logo {
    background-image: url("https://gitlab.com//RaitaroH/DuckDuckGo-DeepDark/raw/master/Images/logo.png") !important;
  }
  .svg .logo_homepage {
    background-image: url("https://gitlab.com//RaitaroH/DuckDuckGo-DeepDark/raw/master/Images/BigLogo.png") !important;
  }
  .set-header--fixed .header-wrap,
  .header-wrap {
    position: fixed !important;
    width: 100% !important;
    top: 0 !important;
    left: 0 !important;
    right: 0 !important;
    z-index: 100 !important;
    padding-bottom: 0 !important;
  }
  #error_homepage {
    color: var(--dimmer-text) !important;
    background: var(--second-background) !important;
  }
  .zci-wrap {
