/* ==UserStyle==
@name         Windowed Rot13
@namespace    zvava.org
@version      1.0.0
@description  window dark theme for for rot13.com
@author       zvava
@preprocessor stylus
@var          color backgroundColor "Background Color" #161621
@var          color windowColor     "Window Color"     #212126
@var          color textColor       "Text Color"       #dddddd
==/UserStyle== */

@-moz-document domain("rot13.com") {
	body {
		width: 100vw;
		height: 100vh;
		background: backgroundColor;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		overflow: hidden;
	}

	* {
		background: transparent;
		color: textColor;
		font-family: monospace;
	}

	h1, h2, p.arrow{
		display: none;
	}

	p {
		margin: 1em 0;
	}

	p::before {
		content: "› untitled";
		padding: 4px 8px;
		background: windowColor;
		display: block;
		text-align: left;
		font-size: 11px;
	}

	p:nth-child(4)::before { content: "› input text"; }
	p:nth-child(6)::before { content: "› mode"; }
	p:nth-child(8)::before { content: "› output text"; }

	textarea {
		width: 25vw;
		height: 15vh;
		margin: 0;
		padding: 3em;
		border: 1px solid windowColor;
		border-top: none;
		resize: horizontal;
	}

	select {
		width: 100%;
		padding: 1em;
		border: 1px solid windowColor;
		border-top: none;
		-webkit-appearance: none;
	       -moz-appearance: none;
	            appearance: none;
	}
}

