/* ==UserStyle==
@name         Windowed Spotify
@namespace    zvava.org
@version      1.0.0
@description  windowed dark theme for spotify
@author       zvava
@preprocessor stylus
@var          color backgroundColor "Background Color" #161621
@var          color windowColor     "Window Color"     #212126
@var          color textColor       "Text Color"       #dddddd
@var          range windowMargin    "Margin"           [1, 0, 15]
==/UserStyle== */

@-moz-document regexp("https://open.spotify.com.*") {
	:root {
		font-size: 1em !important;
	}

	body {
		color: textColor;
		overflow: hidden;
	}

	/* font */
	body, button, input { letter-spacing: initial !important; }
	*, .UserWidget__user-link, .btn {
