		height: 100%;
		margin-top: 0;
		display: flex;
		justify-content: flex-end;
		flex-direction: row-reverse;
		align-content: center;
	}

	/* main view smol view */
	@media (max-width:1199px) {
		.TrackListHeader {
			height: 7rem;
			padding: 0.5rem 1.5rem;
			margin-bottom: 1em;
		}

		.cover-art .cover-art-playback {
			width: 3rem;
			height: 3rem;
			margin-left: -1.5rem;
			margin-top: -1.5rem;
		}

		.cover-art:not(:hover) .cover-art-playback.playing { opacity: 0; }
		.media-object-hoverable.playing:not(:hover) .cover-art-image { filter: brightness(1); }

		.TrackListHeader .media-object { min-width: 0; }
		.TrackListHeader .media-object, 	.TrackListHeader .media-object .media-object-hoverable,
		.TrackListHeader .media-object .media-object-hoverable .react-contextmenu-wrapper .cover-art,
		.TrackListHeader .media-object .media-object-hoverable .react-contextmenu-wrapper .cover-art .cover-art-image {
			width: 5rem;
			height: 5rem;
		}

		.TrackListHeader__entity-name {
			height: 2rem;
			display: flex;
			align-items: center;
		}

		.TrackListHeader__entity-name h2::after {
			content: " ";
			white-space: pre;
		}

		.TrackListHeader__entity-additional-info { display: none; }
	}

	/* hide copyright banner */
	.copyrights { display: none; }
}

